import { useState } from "react";
import styled from "styled-components";
import "./Tags.css";

function Tag() {
  const [tags, setTags] = useState(["javascript", "react"]); //태그를 담을 배열
  const [tag, setTag] = useState("");
  const removeTag = (i) => {
    const clonetags = tags.slice();
    clonetags.splice(i, 1);
    setTags(clonetags);
  };
  const addTag = (e) => {
    setTag(e.target.value);
  };
  const handleKeyPress = (e) => {
    if (e.key === "Enter") {
      handleClick();
    }
  };
  const handleClick = () => {
    setTags([...tags, tag]);
    setTag("");
  };

  return (
    <>
      <div key={i} className="Box">
        <div className="tag">
          <span>{e}</span>
          <button onClick={() => removeTag(i)}>x</button>
        </div>
      </div>
    </>
  );
}

export default Tag;
